import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Post } from "src/app/models/posts";
import { PostsService } from "src/app/services/posts.service";
import { Location } from "@angular/common";
import { FormGroup, FormControl } from "@angular/forms";

@Component({
  selector: "app-editing-posts",
  templateUrl: "./editing-posts.component.html",
  styleUrls: ["./editing-posts.component.css"],
})
export class EditingPostsComponent implements OnInit {
  post!: Post;
  selectedPostId: string | undefined;
  updatedPost = {
    name: "",
    title: "",
    text: "",
    photo: "",
  };
  registerForm: FormGroup = new FormGroup({}); // ініціалізуємо registerForm

  constructor(
    private postsService: PostsService,
    private route: ActivatedRoute,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.registerForm = new FormGroup({
      name: new FormControl(),
      title: new FormControl(),
      text: new FormControl(),
      photo: new FormControl(),
    });

    this.route.paramMap.subscribe((params) => {
      let id = params.get("id")!;
      this.selectedPostId = id;
      this.postsService.getOnePost(id).subscribe((post: Post) => {
        this.post = post;
      });
    });
  }

  onSubmit(): void {
    if (this.selectedPostId) {
      this.postsService
        .updateData(this.selectedPostId, this.updatedPost)
        .subscribe((post: Post) => {
          this.post = post;
        });
    }
  }

  back() {
    this.location.back();
  }
}
<div class="login-form">
  <form novalidate [formGroup]="registerForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="firstName">First Name</label>
      <input type="text" class="form-control" formControlName="firstName" />
    </div>
    <div class="form-group">
      <label for="lastName">Last Name</label>
      <input type="text" class="form-control" formControlName="lastName" />
    </div>
    <div class="form-group">
      <label for="email">Email address</label>
      <input type="email" class="form-control" formControlName="email" />
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" formControlName="password" />
    </div>
    <button
      type="submit"
      class="btn btn-primary"
      [disabled]="!registerForm.valid"
    >
      Register
    </button>
    <button type="submit" class="btn btn-primary" (click)="back()">
      Go back
    </button>
  </form>
</div>

<div class="card" style="width: 18rem" *ngIf="selectedPostId === post._id">
  <img src="{{ post.photo }}" class="card-img-top" alt="..." />
  <div class="card-body">
    <h5 class="card-title">{{ post.name }}</h5>
    <p class="card-text">{{ post.text }}</p>
  </div>
</div>
