import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";

@Injectable({
  providedIn: "root",
})
export class TranslationService {
  public supportedLanguages = ["en", "uk", "fr", "es"];

  public currentLang = new BehaviorSubject<string>("en");
  currentLang$ = this.currentLang.asObservable();

  public translations: any = {};
  currentLangSubject: any;
  translationService: any;

  constructor() {}
  public getSupportedLanguages(): string[] {
    return this.translationService.supportedLanguages;
  }

  loadTranslations(lang: string): Promise<void> {
    return fetch(`/assets/i18n/${lang}.json`)
      .then((response) => response.json())
      .then((translations) => (this.translations = translations));
  }

  getCurrentLang(): string {
    return this.currentLang.value;
  }

  setLang(lang: string) {
    this.currentLangSubject.next(lang);
  }

  translate(key: string): string {
    return this.translations[key] || key;
  }
}
