import { Component, OnInit } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { Post } from "src/app/models/posts";
import { PostsService } from "src/app/services/posts.service";

@Component({
  selector: "app-change-post-detalis",
  templateUrl: "./change-post-detalis.component.html",
  styleUrls: ["./change-post-detalis.component.css"],
})
export class ChangePostDetalisComponent implements OnInit {
  post!: Post;
  posts: Post[] = [];
  constructor(
    private route: ActivatedRoute,
    private postsService: PostsService
  ) {}
  ngOnInit(): void {
    let id = this.route.snapshot.params["id"];
    this.postsService.getOnePost(id).subscribe((post: Post) => {
      this.post = post;
    });
  }
}
<div class="card" style="width: 18rem">
  <img src="{{ post.photo }}" class="card-img-top" alt="..." />
  <div class="card-body">
    <h5 class="card-title">{{ post.name }}</h5>
    <p class="card-text">{{ post.text }}</p>
  </div>
</div>
