import { HttpClient, HttpErrorResponse } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable, catchError, tap, throwError } from "rxjs";
import { UserModel } from "../models/user.model";
import { Router } from "@angular/router";

@Injectable({
  providedIn: "root",
})
export class UsersService {
  URL = "http://localhost:8080/api";

  constructor(private http: HttpClient, router: Router) {}

  //   login(user: UserModel): Observable<any> {
  //     return this.http
  //       .post(`${this.URL}/login`, user)
  //       .pipe(
  //         tap(this.setToken),
  //         catchError(this.handleError)
  //       ) as Observable<any>;
  //   }

  //   setToken(response: any) {
  //     if (response) {
  //       const expiresDate = new Date(new Date().getTime() + 3600 * 1000);
  //       localStorage.setItem("token", response.token);
  //       localStorage.setItem("token-exp", expiresDate.toString());
  //     } else {
  //       localStorage.clear();
  //     }
  //   }

  login(user: UserModel): Observable<any> {
    return this.http
      .post(`${this.URL}/login`, { email: user.email, password: user.password })
      .pipe(
        tap((response) => {
          if (response && response.token) {
            this.setToken(response.token);
          }
        }),
        catchError(this.handleError)
      ) as Observable<any>;
  }

  setToken(token: string) {
    const expiresDate = new Date(new Date().getTime() + 3600 * 1000);
    localStorage.setItem("token", token);
    localStorage.setItem("token-exp", expiresDate.toString());
  }
  login(user: UserModel): Observable<any> {
    return this.http
      .post(`${this.URL}/login`, { email: user.email, password: user.password })
      .pipe(
        tap((response) => {
          if (response && response.token) {
            this.setToken(response.token);
            this.router.navigate(["/profile"]); // перенаправлення на сторінку profile
          }
        }),
        catchError(this.handleError)
      ) as Observable<any>;
  }

  handleError(error: HttpErrorResponse) {
    const message = error.error.errorMessage;
    return throwError(() => new Error(message));
  }

  logout() {
    this.setToken(null);
  }

  getAll(): Observable<UserModel[]> {
    return this.http
      .get<UserModel[]>(this.URL)
      .pipe(catchError(this.handleError));
  }

  getById(id: string): Observable<UserModel> {
    const url = `${this.URL}/${id}`;
    return this.http.get<UserModel>(url).pipe(catchError(this.handleError));
  }

  create(user: UserModel): Observable<UserModel> {
    return this.http
      .post<UserModel>(this.URL, user)
      .pipe(catchError(this.handleError));
  }

  update(id: string, user: UserModel): Observable<UserModel> {
    const url = `${this.URL}/${id}`;
    return this.http
      .put<UserModel>(url, user)
      .pipe(catchError(this.handleError));
  }

  delete(id: string): Observable<void> {
    const url = `${this.URL}/${id}`;
    return this.http.delete<void>(url).pipe(catchError(this.handleError));
  }

  isAuthenticated(): boolean {
    return !!this.token;
  }

  get token(): string | null {
    const expStr = localStorage.getItem("token-exp");

    if (expStr) {
      const date = new Date(expStr);

      if (date < new Date()) {
        this.logout();
        return null;
      } else {
        return localStorage.getItem("token");
      }
    }

    return null;
  }
}
