import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class LikesService {
  constructor() {}

  private getLikes(): { [postId: string]: boolean } {
    const likesString = localStorage.getItem("likes");
    if (!likesString) {
      return {};
    }
    return JSON.parse(likesString);
  }

  private saveLikes(likes: { [postId: string]: boolean }) {
    localStorage.setItem("likes", JSON.stringify(likes));
  }

  private updateLikes(postId: string, value: boolean) {
    const likes = this.getLikes();
    likes[postId] = value;
    this.saveLikes(likes);
  }

  public getLikesCount(postId: string): number {
    const likes = this.getLikes();
    return likes[postId] ? 1 : 0;
  }

  public toggleLike(postId: string): void {
    const likes = this.getLikes();
    const value = !likes[postId];
    this.updateLikes(postId, value);
  }
}
