import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Post } from "src/app/models/posts";
import { PostsService } from "src/app/services/posts.service";
import { Location } from "@angular/common";
import { FormGroup, FormControl } from "@angular/forms";

@Component({
  selector: "app-editing-posts",
  templateUrl: "./editing-posts.component.html",
  styleUrls: ["./editing-posts.component.css"],
})
export class EditingPostsComponent implements OnInit {
  post!: Post;
  selectedPostId: string | undefined;
  registerForm!: FormGroup;

  constructor(
    private postsService: PostsService,
    private route: ActivatedRoute,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.registerForm = new FormGroup({
      name: new FormControl(),
      title: new FormControl(),
      text: new FormControl(),
      photo: new FormControl(),
    });

    this.route.paramMap.subscribe((params) => {
      let id = params.get("id")!;
      this.selectedPostId = id;
      this.postsService.getOnePost(id).subscribe((post: Post) => {
        this.post = post;
        this.registerForm.patchValue({
          name: this.post.name,
          title: this.post.title,
          text: this.post.text,
          photo: this.post.photo,
        });
      });
    });
  }

  onSubmit(): void {
    if (this.selectedPostId) {
      const updatedPost = {
        name: this.registerForm.value.name,
        title: this.registerForm.value.title,
        text: this.registerForm.value.text,
        photo: this.registerForm.value.photo,
      };
      this.postsService
        .updateData(this.selectedPostId, updatedPost)
        .subscribe((post: Post) => {
          this.post = post;
          console.log(post);
        });
    }
  }

  back() {
    this.location.back();
  }
}
<div class="container">
  <div class="row mainRow">
    <div class="col col-6">
      <div class="editing-form">
        <form novalidate [formGroup]="registerForm" (ngSubmit)="onSubmit()">
          <div class="form-group">
            <label for="name">Name</label>
            <input type="text" class="form-control" formControlName="name" />
          </div>
          <div class="form-group">
            <label for="title">Title</label>
            <input type="text" class="form-control" formControlName="title" />
          </div>
          <div class="form-group">
            <label for="text">Text</label>
            <textarea class="form-control" formControlName="text"></textarea>
          </div>
          <div class="form-group">
            <label for="photo">Photo</label>
            <input type="text" class="form-control" formControlName="photo" />
          </div>
          <div class="buttonDiv">
            <button
              type="submit"
              class="btn btn-primary"
              [disabled]="!registerForm.valid"
            >
              Save Editings
            </button>
            <button type="button" class="btn btn-secondary" (click)="back()">
              Back
            </button>
          </div>
        </form>
      </div>
    </div>
    <div class="col col-6">
      <div class="card" *ngIf="post && selectedPostId === post._id">
        <div class="row g-0">
          <div class="col-md-6 photo">
            <img src="{{ post.photo }}" class="card-img-top" alt="..." />
          </div>
          <div class="col-md-6">
            <div class="card-body">
              <h5 class="card-title">{{ post.name }}</h5>
              <p class="card-text">
                <small class="text-body-secondary">{{ post.title }}</small>
              </p>
              <p class="card-text">{{ post.text }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
