import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class LikesService {
  private storageKey = "postLikes";

  constructor() {}

  getLikesFromStorage(): { [postId: string]: number } {
    const likesString = localStorage.getItem(this.storageKey);
    return likesString ? JSON.parse(likesString) : {};
  }

  saveLikesToStorage(likes: { [postId: string]: number }): void {
    localStorage.setItem(this.storageKey, JSON.stringify(likes));
  }
}
