import { Component } from "@angular/core";
import { TranslateService } from "@ngx-translate/core";
// import { TranslationService } from "src/app/services/translation.service";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.css"],
})
export class FooterComponent {
  constructor(public translate: TranslateService) {}
  // currentLang: string = "en";
  // constructor(public translationService: TranslationService) {}
  // public setLang(lang: string) {
  //   this.translationService.setLang(lang);
  //   this.currentLang = lang;
  //   console.log(lang);
  // }
  // public getSupportedLanguages() {
  //   return this.translationService.supportedLanguages;
  // }
}
<footer class="d-flex flex-wrap justify-content-between align-items-center">
  <div class="container text-center">
    <div class="row align-items-start">
      <div class="col col-3">
        <div class="container text-center">
          <div class="row align-items-start">
            <div class="col col-4">
              <img src="assets/images/blogg.webp" width="80" alt="" />
            </div>
            <div class="col col-8">
              <div class="dropdown">
                <select (change)="translate.use($event.target.value)">
                  <option value="en">English</option>
                  <option value="uk">Українська</option>
                </select>
                <!-- <select
                  [(ngModel)]="currentLang"
                  (change)="setLang(currentLang)"
                >
                  <option
                    *ngFor="let lang of getSupportedLanguages()"
                    [value]="lang"
                  >
                    {{ lang }}
                  </option>
                </select> -->
              </div>
            </div>
          </div>
        </div>
      </div>
      <div class="col col-3"><app-google-map></app-google-map></div>
      <div class="col col-6">
        <div class="container text-center">
          <div class="row align-items-start">
            <div class="col col-4">
              <h5>PRODUCTS</h5>
              <a class="text-body-secondary" href="#"><p>FEATURE LIST</p></a>
              <a class="text-body-secondary" href="#"><p>PRICING</p></a>
              <a class="text-body-secondary" href="#"><p>DOMAINS</p></a>
              <a class="text-body-secondary" href="#"><p>WEBSITES</p></a>
              <a class="text-body-secondary" href="#"><p>DOMAINS</p></a>
              <a class="text-body-secondary" href="#"><p>ONLINE STORES</p></a>
            </div>
            <div class="col col-4">
              <h5>CUSTOMERS</h5>
              <a class="text-body-secondary" href="#"><p>STUDENTS</p></a>
              <a class="text-body-secondary" href="#"><p>ENTERPRISE</p></a>
              <a class="text-body-secondary" href="#"><p>WEDDINGS</p></a>
              <a class="text-body-secondary" href="#"><p>PHOTOGRAPHERS</p></a>
              <a class="text-body-secondary" href="#"><p>BLOGGERS</p></a>
              <a class="text-body-secondary" href="#"><p>ARTISTS</p></a>
              <a class="text-body-secondary" href="#"><p>RESTAURANTS</p></a>
            </div>
            <div class="col col-4">
              <h5>SOCIAL NETWORKS</h5>
              <div class="social">
                <a class="text-body-secondary" href="#"
                  ><i class="bi bi-facebook"></i
                ></a>
                <a class="text-body-secondary" href="#"
                  ><i class="bi bi-instagram"></i
                ></a>
                <a class="text-body-secondary" href="#"
                  ><i class="bi bi-twitter scale"></i
                ></a>
                <a class="text-body-secondary" href="#"
                  ><i class="bi bi-envelope-fill"></i>
                </a>
                <a class="text-body-secondary" href="#"
                  ><i class="bi bi-google"></i
                ></a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</footer>
