import { Component } from "@angular/core";
import { Post } from "src/app/models/posts";
import { LikesService } from "src/app/services/LikesService.service";

@Component({
  selector: "app-like",
  templateUrl: "./like.component.html",
  styleUrls: ["./like.component.css"],
})
export class LikeComponent {
  posts: Post[] = [
    {
      _id: 1,
      title: "Post 1",
      content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      likes: 0,
    },
    {
      _id: 2,
      title: "Post 2",
      content:
        "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
      likes: 0,
    },
    {
      _id: 3,
      title: "Post 3",
      content:
        "Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.",
      likes: 0,
    },
    {
      _id: 4,
      title: "Post 4",
      content:
        "Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.",
      likes: 0,
    },
    {
      _id: 5,
      title: "Post 5",
      content:
        "Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
      likes: 0,
    },
  ];

  postLikes: { [postId: number]: number } = {};

  constructor(private likesService: LikesService) {}

  ngOnInit() {
    this.likesService.loadLikesFromStorage();
    this.posts.forEach((post) => {
      this.postLikes[post._id] = this.likesService.getLikes(post._id) || 0;
    });
  }

  addLike(postId: number) {
    this.postLikes[postId]++;
    this.likesService.saveLikesToStorage(this.postLikes);
  }

  getTopFivePosts() {
    const sortedPosts = [...this.posts].sort((a, b) => b.likes - a.likes);
    return sortedPosts.slice(0, 5);
  }
}
