import { Component, OnInit } from "@angular/core";
import { forkJoin } from "rxjs";
import { Post } from "src/app/models/posts";
import { PostsService } from "src/app/services/posts.service";
import { LikesService } from "src/app/services/LikesService.service";
import { map, mergeMap } from "rxjs/operators";

@Component({
  selector: "app-top10",
  templateUrl: "./top10.component.html",
  styleUrls: ["./top10.component.css"],
})
export class Top10Component implements OnInit {
  topPosts: Post[] = [];

  constructor(
    private postsService: PostsService,
    private likesService: LikesService
  ) {}

  ngOnInit(): void {
    this.postsService
      .getPosts()
      .pipe(
        mergeMap((posts) => {
          const likes$ = posts.map((post) =>
            this.likesService.getLikes(post._id).pipe(
              map((likes) => ({
                ...post,
                likes: likes && likes.likes ? likes.likes : null,
              }))
            )
          );
          return forkJoin(likes$).pipe(
            map((postsWithLikes) =>
              postsWithLikes.sort((a, b) => b.likes - a.likes).slice(0, 10)
            )
          );
        })
      )
      .subscribe((topPosts) => {
        this.topPosts = topPosts;
      });
  }
}
