import { Component } from "@angular/core";
import { PostsService } from "src/app/services/posts.service";

@Component({
  selector: "app-new-posts",
  templateUrl: "./new-posts.component.html",
  styleUrls: ["./new-posts.component.css"],
})
export class NewPostsComponent {
  name: string = "";
  title: string = "";
  text: string = "";
  photo: string = "";

  constructor(private postsService: PostsService) {}

  createPost() {
    const postData = {
      name: this.name,
      title: this.title,
      text: this.text,
      photo: this.photo,
    };

    this.postsService.createPost(postData).subscribe(
      (result) => {
        console.log(result);
      },
      (error) => {
        console.error(error);
      }
    );
  }
}
<form (ngSubmit)="createPost()">
  <label for="name">Name:</label>
  <input type="text" id="name" [(ngModel)]="name" name="name" required />

  <label for="title">Title:</label>
  <input type="text" id="title" [(ngModel)]="title" name="title" required />

  <label for="text">Text:</label>
  <textarea id="text" [(ngModel)]="text" name="text" required></textarea>

  <label for="photo">Photo:</label>
  <input type="text" id="photo" [(ngModel)]="photo" name="photo" />

  <button type="submit">Create Post</button>
</form>
