import { Component, OnInit } from "@angular/core";
import { Post } from "src/app/models/posts";
import { LikesService } from "src/app/services/LikesService.service";

@Component({
  selector: "app-like",
  templateUrl: "./like.component.html",
  styleUrls: ["./like.component.css"],
})
export class LikeComponent {
  posts: Post[] = [];

  constructor(private likesService: LikesService) {}

  addLike(postId: number) {
    const likes = this.likesService.getLikes(postId) || 0;
    this.likesService.saveLikes({
      ...this.likesService.postLikes,
      [postId]: likes + 1,
    });
  }

  getTopFivePosts() {
    const sortedPosts = [...this.posts].sort(
      (a, b) =>
        (this.likesService.getLikes(parseInt(b._id)) || 0) -
        (this.likesService.getLikes(parseInt(a._id)) || 0)
    );
    return sortedPosts.slice(0, 5);
  }
}
