import { Component, OnInit } from "@angular/core";
import {
  FormControl,
  FormBuilder,
  FormGroup,
  Validators,
} from "@angular/forms";
import { Router } from "@angular/router";
import { UsersService } from "../../services/users.service";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.css"],
})
export class LoginComponent implements OnInit {
  loginForm: FormGroup = new FormGroup({
    email: new FormControl(null, Validators.required),
    password: new FormControl(null, Validators.required),
  });

  constructor(
    private formBuilder: FormBuilder,
    private usersService: UsersService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      email: [""],
      password: [""],
    });
  }

  onSubmit() {
    if (this.loginForm.valid) {
      const { email, password } = this.loginForm.value;
      console.log("zzzz", email, password);
      this.usersService.login(email, password).subscribe(
        (response) => {
          console.log(response);
        },
        (error) => {
          console.log(error);
        }
      );
    }
  }

  newMember() {
    console.log("register clicked");
    this.router.navigate(["/admin-page/register"]);
  }
}
<form novalidate [formGroup]="loginForm" (ngSubmit)="onSubmit()">
  <div class="login-form">
    <div class="register">
      <h2>Login to your Account</h2>
      <h2>or</h2>
      <h2>Become a new member</h2>
      <button (click)="newMember()" type="button" class="btn btn-primary">
        Register
      </button>
    </div>

    <div class="form-group">
      <label for="email"></label>
      <input
        type="email"
        id="email"
        name="email"
        placeholder="Enter your email"
        required
      />
    </div>
    <div class="form-group">
      <label for="password"></label>
      <input
        type="password"
        id="password"
        name="password"
        placeholder="Enter your password"
        required
      />
    </div>
    <button type="submit">Login</button>
  </div>
</form>
