import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export interface LikesResponse {
    [postId: number]: number;
  }

  @Injectable({
    providedIn: 'root',
  })
  export class LikesService {
    private storageKey = 'likes';

    constructor() {}

    addLike(postId: number): void {
      const likes = this.getLikesFromStorage();
      likes[postId] = true;
      this.saveLikesToStorage(likes);
    }

    unlikePost(postId: number): void {
      const likes = this.getLikesFromStorage();
      delete likes[postId];
      this.saveLikesToStorage(likes);
    }

    getLikes(): LikesResponse {
      const likes = JSON.parse(localStorage.getItem(this.storageKey) || '{}');
      return likes;
    }

