import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { Post } from "src/app/models/posts";
import { LikesService } from "src/app/services/likes.service"; // поправлено імпорт
import { PostsService } from "src/app/services/posts.service";

@Component({
  selector: "app-home",
  templateUrl: "./home.component.html",
  styleUrls: ["./home.component.css"],
  providers: [LikesService], // додано сервіс до providers
})
export class HomeComponent implements OnInit {
  likeCount = 0;
  time: string = new Date().toLocaleString();

  posts: Post[] = [];
  constructor(
    private postService: PostsService,
    private router: Router,
    private likesService: LikesService
  ) {}
  ngOnInit(): void {
    this.postService.getPosts().subscribe((posts: Post[]) => {
      this.posts = posts;
    });
    this.likesService.getLikes().subscribe((likes: number) => {
      // додано тип до параметра likes
      this.likeCount = likes;
    });
  }

  postDetalis(_id: string): void {
    this.router.navigate(["/content-page/detalis", _id]);
  }
}
