import { Component, OnInit } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { Post } from "src/app/models/posts";
import { PostsService } from "src/app/services/posts.service";
import { Location } from "@angular/common";

@Component({
  selector: "app-post-detalis",
  templateUrl: "./post-detalis.component.html",
  styleUrls: ["./post-detalis.component.css"],
})
export class PostDetalisComponent implements OnInit {
  post!: Post;
  posts: Post[] = [];
  constructor(
    private route: ActivatedRoute,
    private postsService: PostsService,
    private location: Location,
    private router: Router
  ) {}

  ngOnInit(): void {
    let id = this.route.snapshot.params["id"];
    this.postsService.getOnePost(id).subscribe((post: Post) => {
      this.post = post;
    });
    this.postsService.getPosts().subscribe((posts: Post[]) => {
      this.posts = posts;
    });
  }

  back() {
    this.location.back();
  }

  // onePost(_id: string) {
  //   console.log(_id);
  //   this.router.navigate(["/content-page/detalis", _id]);
  // }
  selectedPost: any;

  onePost(postId: string) {
    // ваш код, який знаходить вибраний post за його ідентифікатором
    this.selectedPost = selectedPost;
  }
}
